import React, { useState, useEffect, useCallback } from 'react'
import './App.css'
import { BookmarkPlus, Trash2 } from 'lucide-react'

function App () {
  const [bookmarks, setBookmarks] = useState([])
  const [url, setUrl] = useState('')
  const [title, setTitle] = useState('')

  const saveBookmarks = useCallback((updated) => {
    localStorage.setItem('bookmarks', JSON.stringify(updated))
  }, [])

  useEffect(() => {
    const local = localStorage.getItem('bookmarks')
    if (local) {
      setBookmarks(JSON.parse(local))
    } else {
      fetch('/bookmarks.json')
        .then(res => {
          if (!res.ok) throw new Error('Failed to load bookmarks')
          return res.json()
        })
        .then(data => {
          setBookmarks(data)
          localStorage.setItem('bookmarks', JSON.stringify(data))
        })
        .catch(err => {
          console.error('Error loading bookmarks:', err)
        })
    }
  }, [])

  const handleAdd = (e) => {
    e.preventDefault()
    if (!url || !title) return

    const newBookmark = {
      id: Date.now(),
      title,
      url
    }

    const updated = [newBookmark, ...bookmarks]
    setBookmarks(updated)
    saveBookmarks(updated)
    setUrl('')
    setTitle('')
  }

  const handleDelete = (id) => {
    const updated = bookmarks.filter(b => b.id !== id)
    setBookmarks(updated)
    saveBookmarks(updated)
  }

  return (
    <div className='App'>
      <header>
        <h1>Bookmarks</h1>
        <p>Save and manage your favourite links</p>
      </header>

      <form onSubmit={handleAdd} className='add-bookmark'>
        <input
          type='text'
          placeholder='Title'
          value={title}
          onChange={e => setTitle(e.target.value)}
        />
        <input
          type='url'
          placeholder='URL'
          value={url}
          onChange={e => setUrl(e.target.value)}
        />
        <button type='submit'>
          <BookmarkPlus size={16} /> Add Bookmark
        </button>
      </form>

      <ul className='bookmark-list'>
        {bookmarks.map(bookmark => (
          <li key={bookmark.id} className='bookmark-item'>
            <a href={bookmark.url} target='_blank' rel='noopener noreferrer'>
              {bookmark.title}
            </a>
            <button onClick={() => handleDelete(bookmark.id)} className='delete-btn'>
              <Trash2 size={14} />
            </button>
          </li>
        ))}
      </ul>
    </div>
  )
}

export default App
